UPDATE `sgulot` SET `prefix` = NULL, `mqorot` = '<h3>מה ההבדל בין <strong>חכם</strong> ל<strong>נבון</strong>?</h3>\r\n<p><strong>חכם</strong> הוא <a href=\"http://localhost/tnk1/kma/qjrim1/xkm.html\">היודע לשמוע, ללמוד וללמד</a>;&#160;&#160; <strong>נבון</strong> הוא <a href=\"http://localhost/tnk1/kma/qjrim1/bwn.html\">היודע לחשוב ולהסיק מסקנות</a>.</p><p>החכם <strong>יוסף לֶקַח</strong>       = ייקח מידע מבחוץ ויוסיף על חכמתו;&#160;&#160; והנבון <strong>יקנה&#160;תחבולות</strong> = ישיג לעצמו שיטות חדשות לחשיבה והסקת מסקנות.</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 1 AND `verse_number` = 5 AND 1 LIMIT 1;

