UPDATE `sgulot` SET `prefix` = NULL, `verse_text` = 'ספר מִשְׁלֵי <span style=\'font-size:20px\'>עם</span>', `mcudot` = '<p>תרגום עברי קצר לכל פסוק, המבוסס על פירושי מצודת דוד ומצודת ציון.</p>', `tirgum` = '<p>תרגום עברי חדש לכל פרק ופסוק, מאת אראל סגל-הלוי.</p>', `main_qod_subjects` = NULL, `mqorot` = '<p>נימוקים והרחבות על התרגום, המתבססים על ניתוח מבנה הפרק והפסוק ועל ניתוח מילים ומושגים בפסוק, לפי פרשנים שונים.</p>', `tosfot` = NULL, `kotrt` = NULL WHERE `book` = 'משל' AND `chapter_number` = 99 AND `verse_number` = 0 AND 1 LIMIT 1;

