UPDATE `sgulot` SET `prefix` = NULL, `main_qod_subjects` = NULL, `mqorot` = '<p>נימוקים מפורטים לתרגום החדש, המתבססים על ניתוח מבנה הפרק והפסוק, ניתוח משמעות של מילים ומושגים והשוואה בין פרשנים שונים.</p>', `tosfot` = NULL, `kotrt` = NULL WHERE `book` = 'משל' AND `chapter_number` = 99 AND `verse_number` = 0 AND 1 LIMIT 1;

