UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '<p>הדברים האלה נאמרים על שני דרכים: <strong>להבין את המשל</strong> - מה שהמשיל אל המליצה, וגם את <strong>המליצה</strong> עצמה: כשאומר בהמליצה להשמר מזונה, ואמורה להמשיל על המינות, הנה יש להבין בה להיות נזהר ממינות, גם מאשה זונה;</p><p>(ולתוספת ביאור אמר):&#160;&#160; <strong>דברי חכמים </strong>האמורים בפה מלא, והרמוזים בדרך <strong>חידה</strong> (דברים סתומים קשה ההבנה).</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 1 AND `verse_number` = 6 AND 1 LIMIT 1;

