CREATE TABLE `sgulot` (
  `book` char(3) NOT NULL DEFAULT '',
  `chapter_letter` char(3) DEFAULT NULL,
  `chapter_number` int(11) NOT NULL DEFAULT '0',
  `verse_number` int(11) NOT NULL DEFAULT '0',
  `prefix` varchar(63) DEFAULT NULL,
  `main_qod` varchar(32) DEFAULT NULL,
  `verse_text` text,
  `pages` tinyint(4) DEFAULT '1',
  `mcudot` text,
  `tirgum` text,
  `main_qod_subjects` varchar(255) DEFAULT NULL,
  `mqorot` text,
  `ecot` text,
  `hqblot` text,
  `dquyot` text,
  `tosfot` text,
  `full` text,
  `kotrt` varchar(255) DEFAULT NULL,
  PRIMARY KEY (`book`,`chapter_number`,`verse_number`)
) ENGINE=MyISAM CHARACTER SET utf8;

SET character_set_database=utf8;

LOAD DATA LOCAL INFILE '$BACKUP_FILEROOT/sgulot.txt'  INTO TABLE sgulot (book,chapter_letter,chapter_number,verse_number,prefix,main_qod,verse_text,pages,mcudot,tirgum,main_qod_subjects,mqorot,ecot,hqblot,dquyot,tosfot,full,kotrt);

SET character_set_client=utf8;
SET character_set_connection=utf8;
UPDATE `sgulot` SET `verse_text` = 'אָז תֵּלֵךְ לָבֶטַח דַּרְכֶּךָ,\r\nוְרַגְלְךָ לֹא תִגּוֹף;', `mcudot` = '\r\n<p>- כשתהיה נשמר במעשה ובמחשבה, <strong>אז תלך לבטח בדרכיך</strong>, כי אין מחריד;&#160;&#160;&#160; <strong>ורגלך לא תגוף</strong> (תוכה) באבני מכשול המושלכים בדרך, כלומר: לא תאונה אליך רעה.\r\n\r\n</p>', `tirgum` = '<p>אם תתכנן את מעשיך בחכמה, <strong>אז</strong> <strong>תלך</strong>     \r\n<strong>בבטחון</strong> ובשלוה<strong> בדרך </strong>להשגת מטרותיך; &#160;&#160; <strong>ורגלך</strong> <strong>לא</strong> <strong>תִגּוֹף</strong> (תתקל במכשולים), שהרי התכוננת מראש לכל בעיה אפשרית;</p>', `hqblot` = '<p>בתחילת הפרק נאמר, <a class=\"psuq\" href=\"http://tora.us.fm/tnk1/prqim/t2803.htm#5\">משלי ג5-6</a>: \"<q class=\"psuq\"><strong>בְּטַח</strong> אֶל ה\' <em>בְּכָל לִבֶּךָ</em>, וְאֶל <strong>בִּינָתְךָ</strong> אַל תִּשָּׁעֵן;&#160; בְּכָל <strong>דְּרָכֶיךָ</strong> דָעֵהוּ, וְהוּא יְיַשֵּׁר אֹרְחֹתֶיךָ</q>\" (<a href=\"http://tora.us.fm/tnk1/ktuv/mj/03-06.html\">פירוט</a>) - הבטחון בדרך נובע מקשר עם ה\' בתפילה מהלב.&#160;&#160;&#160; בפסוקים שלנו נאמר, <a class=\"psuq\" href=\"http://tora.us.fm/tnk1/prqim/t2803.htm#19\">משלי ג19-23</a>: \"<q class=\"psuq\">ה\' בְּחָכְמָה יָסַד אָרֶץ, כּוֹנֵן שָׁמַיִם <strong>בִּתְבוּנָה</strong>... </q><q class=\"psuq\">בְּנִי, אַל יָלֻזוּ <em>מֵעֵינֶיךָ</em>... </q><q class=\"psuq\">אָז תֵּלֵךְ <strong>לָבֶטַח דַּרְכֶּךָ</strong></q>\" (<a href=\"http://tora.us.fm/tnk1/ktuv/mj/03-19.html\">פירוט</a>) - הבטחון בדרך נובע מראיית התבונה האלהית שבה נברא העולם<small class=\"small\"> (ע\"פ גליה)</small>.</p>', `tosfot` = NULL, `full` = 'רמי ניר, 6.5.07: \"כדאי לזכור מה אמר קהלת על חכמה ושימושיה: \"כִּי\r\nבְּרֹב חָכְמָה רָב-כָּעַס, וְיוֹסִיף דַּעַת יוֹסִיף מַכְאוֹב\" (קהלת א\'\r\n18). צריך לקחת בחשבון: לא קל להשתמש בחכמה! לדוגמא: \"כִּי-יֵשׁ אָדָם\r\nשֶׁעֲמָלוֹ בְּחָכְמָה וּבְדַעַת--וּבְכִשְׁרוֹן, וּלְאָדָם שֶׁלֹּא\r\nעָמַל-בּוֹ יִתְּנֶנּוּ חֶלְקוֹ, גַּם-זֶה הֶבֶל וְרָעָה רַבָּה\" (קהלת ב\' 21).  \r\n<p>עדים זוממים שואפים לממש את הרעיון הזה; ואמנם: \"וְעוֹד רָאִיתִי\r\nתַּחַת הַשָּׁמֶשׁ: מְקוֹם הַמִּשְׁפָּט שָׁמָּה הָרֶשַׁע, וּמְקוֹם\r\nהַצֶּדֶק שָׁמָּה הָרָשַׁע\" (קהלת ג\' 16) כפי שזועק הנביא: \"\r\n<q class=\"psuq\">כִּי רָשָׁע מַכְתִּיר אֶת-הַצַּדִּיק, עַל-כֵּן יֵצֵא מִשְׁפָּט מְעֻקָּל</q> \" (חבקוק א\' 4). כלומר, תתכן אפשרות כי שופטים יעשו יד אחת עם עדים\r\nזוממים... \"כִּי יֵשׁ-דְּבָרִים הַרְבֵּה מַרְבִּים הָבֶל, מַה-יֹּתֵר\r\nלָאָדָם\" (קהלת ו\' 11).</p>  \r\n<p>וחייב לדעת גם המשתמש בחכמה: \"כִּי גַּם לֹא-יֵדַע הָאָדָם אֶת-עִתּוֹ,\r\nכַּדָּגִים שֶׁנֶּאֱחָזִים בִּמְצוֹדָה רָעָה וְכַצִּפֳּרִים הָאֲחֻזוֹת\r\nבַּפָּח, כָּהֵם יוּקָשִׁים בְּנֵי הָאָדָם לְעֵת רָעָה כְּשֶׁתִּפּוֹל\r\nעֲלֵיהֶם פִּתְאֹם\" (קהלת ט\' 12).</p>  \r\n<p>\"שַׁלַּח לַחְמְךָ עַל-פְּנֵי הַמָּיִם, כִּי-בְרֹב הַיָּמִים\r\nתִּמְצָאֶנּוּ... תֶּן-חֵלֶק לְשִׁבְעָה וְגַם לִשְׁמוֹנָה, כִּי לֹא\r\nתֵדַע מַה-יִּהְיֶה רָעָה עַל-הָאָרֶץ\" (קהלת יא\' 2-1).</p><p>  \r\n\"סוֹף דָּבָר הַכֹּל נִשְׁמָע, אֶת-הָאֱלֹהִים יְרָא וְאֶת-מִצְוֹתָיו שְׁמוֹר, כִּי-זֶה כָּל-הָאָדָם\" (קהלת יב\' 13).</p>' WHERE `book` = 'משל' AND `chapter_number` = 3 AND `verse_number` = 23 AND 1 LIMIT 1;

SET character_set_client=utf8;
SET character_set_connection=utf8;
UPDATE `sgulot` SET `verse_text` = 'אִם תִּשְׁכַּב לֹא תִפְחָד,\r\nוְשָׁכַבְתָּ וְעָרְבָה שְׁנָתֶךָ.', `mcudot` = '<p><strong>אם תשכב לא תפחד, ושכבת - וערבה</strong> (מתקה)<strong> שנתך</strong>, כדרך הישן בוטח מבלי פחד.</p>\r\n\r\n\r\n\r\n', `tirgum` = '<p> כאשר <strong>תשכב</strong> לישון - <strong>לא</strong> תהיה טרוד ב<strong>פחדים</strong>;&#160;&#160;&#160; מייד כש<strong>תשכב</strong> - תירדם ותישן <strong>שֵנָה</strong>  <strong>עֲרֵבָה</strong> (מתוקה ונעימה);</p>\r\n', `mqorot` = '<p>מה הקשר בין החכמה והתבונה, הנזכרות בפסוקים הקודמים, לבין השֵׁנָה הערֵבָה, הנזכרת בפסוקנו?</p><p>1. אדם שתיכנן את כל מעשיו בחכמה ותבונה, אינו צריך לפחד מהפתעות וצרות, ויכול לישון בשקט; כמו ב<a class=\"psuq\" href=\"http://tora.us.fm/tnk1/prqim/t2801.htm#33\">משלי א33</a>: \"<q class=\"psuq\">וְשֹׁמֵעַ לִי יִשְׁכָּן בֶּטַח, וְשַׁאֲנַן מִפַּחַד רָעָה</q>\" (<a href=\"http://tora.us.fm/tnk1/ktuv/mj/01-33.html\">פירוט</a>).</p> \r\n<p>2. כשאדם לומד דברי-חכמה לפני השינה, הנפש נרגעת והחלומות מתוקים.</p>', `ecot` = '<p>הפסוקים מלמדים איך לזכות לשינה ערבה ומתוקה, ללא פחדים וחלומות רעים:</p><p>1. לתכנן את כל המעשים בחכמה ותבונה (ראו <a href=\"/tnk1/ktuv/mj/03-21.html\">בפסוקים הקודמים</a>); כך אין צורך לפחד מהפתעות וצרות, ואפשר לישון בשקט.</p> \r\n<p>2. ללמוד דברי חכמה ותורה לפני השינה; כך הנפש נרגעת והחלומות מתוקים.</p><p>3. לקרוא קריאת שמע על המיטה: \"<q class=\"mfrj\">שלמה המלך מגלה לנו איך להגיע למצב של \r\n<strong>אם תשכב לא תפחד</strong>    - כשתעלה על מיטתך לא ילווך פחדים וחלומות רעים, אלא \r\n<strong>ושכבת וערבה שנתך</strong>    - השינה תתן לך ערבות ומתיקות וכוח רוחני חיובי. כיצד? לפני השינה תן לנשמתך צידה לדרך ושמירה רוחנית... אמור \r\n<strong>קריאת שמע על המיטה</strong>. כאשר האדם קורא מתוך הסידור את קריאת שמע, עם \r\nכל הפסוקים והסגולות הכתובות שם, בכוונה ותשומת לב - הרי שנשמתו מקבלת הגנה\r\n מאלקים חיים, וכמו שמסיימים בקריאת שמע על המיטה: \r\n<strong>בידך אפקיד רוחי</strong>...</q>\" \r\n<small>(הרב רונן חזיזה, חמש דקות תורה ביום, יד סיון ה\'תשס\"ט)</small>. </p>', `hqblot` = '<p>יעקב אבינו ברח מפני עשו אחיו לבד ובחוסר-כל. אולם בבריחתו הוא לקח איתו את החכמה האלהית שלמד מהוריו. ולכן הוא <strong>הלך לבטח</strong> אל חרן <strong>ורגלו לא נגפה</strong> בדרך;&#160;&#160; הוא <strong>שכב</strong> לישון באמצע שום-מקום <strong>ולא פחד</strong> מאויביו הרודפים אותו, ואף זכה לחזון נבואי <strong>ערֵב</strong> ומתוק: \"<q class=\"mfrj\"><strong>אז תלך לבטח</strong>    (פסוק 23) - זה יעקב, דכתיב (בראשית כח) \r\n<strong>ויצא יעקב</strong>. &#160; \r\n<strong>אם תשכב לא תפחד</strong>    - מעשו ומלבן. \r\n<strong>ושכבת וערבה שנתך</strong>    - \r\n<strong>וישכב במקום ההוא</strong></q>\" \r\n<small>(רבי פנחס בשם רבי הונא בר פפא, \r\n<a href=\"http://he.wikisource.org/w/index.php?title=%D7%91%D7%A8%D7%90%D7%A9%D7%99%D7%AA%20%D7%A8%D7%91%D7%94%20%D7%A1%D7%97%20%D7%90\">בראשית רבה סח א</a>)</small>,&#160; \"<q class=\"psuq\">והנה ה\' נצב עליו</q>\".</p><p class=\"future\">\r\n\r\n   \r\nבפסוק שלנו נאמר, שההולך בדרך החכמה יכול לישון בשקט; בפרק הבא נאמר, שהרשעים לא יכולים לישון בשקט, \r\n  <a class=\"psuq\" href=\"/tnk1/prqim/t2804.htm#16\">משלי ד16</a>: \"<q class=\"psuq\">כִּי לֹא יִשְׁנוּ אִם לֹא יָרֵעוּ, וְנִגְזְלָה שְׁנָתָם אִם לֹא</q> \r\n \r\n  <q class=\"psuq\">[יַכְשִלוּ]</q>\" (<a href=\"/tnk1/ktuv/mj/04-16.html\">פירוט</a>).</p>', `dquyot` = '<p class=\"future\">1. שני חלקי הפסוק מתארים שתי ברכות שהעוסק בחכמה זוכה בהן - תוצאה שלילית <strong>לא תפחד</strong>, ותוצאה חיובית <strong>וערבה שנתך</strong>.</p>   \r\n<p class=\"future\">2. ניתן גם לפרש שהפסוק הוא תנאי: \r\n<strong>אם תשכב לא תפחד</strong>    - אם תדע להתגבר על הפחד כשאתה שוכב לישון - אז \r\n<strong>ושכבת וערבה שנתך</strong>    - תזכה לשינה ערבה.</p>', `tosfot` = NULL, `full` = '<p><a href=\"/tnk1/ktuv/mj/03-24.html\">המאמר המקורי</a></p>\r\n\r\n<div class=\"future\">\r\n<h2>מאמרים נוספים - באדיבות \r\n<a href=\"http://www.google.com/cse?cx=010178520503316434778%3Aufdjhgdvtao\">גוגל</a></h2>   \r\n<ul>\r\n		\r\n<li>\r\n<a href=\"http://www.beitorot.org/content.asp?PageId=60\">בית אורות - פרשת ויצא - תשס\"ו</a>: כך מלמדנו המדרש רבה (פח\' א\') \"ויצא יעקב, ר\' פנחס בשם רבי הונא בר פפא פתח, \"אז   תלך לבטח דרכך \r\n<strong>אם תשכב לא תפחד</strong>\" (משלי ג\'), אז תלך לבטח זה יעקב, דכתיב ויצא יעקב   \r\n<strong>...</strong>    (<a href=\"http://www.google.com/search?q=cache:3sn-R6P-HzYJ:www.beitorot.org\">cache</a>)</li>   \r\n<li>\r\n<a href=\"http://www.yba.org.il/show.asp?id=24115\">מרכז ישיבות בני עקיבא - פרשת ויצא - לישון בשקט</a>: \r\n<strong>אם תשכב לא תפחד</strong>\'. \'אז תלך לבטח\', זה יעקב, דכתיב \'ויצא יעקב\', \'<strong>אם תשכב לא תפחד</strong>\',   מעשו ומלבן, \'ושכבת וערבה שנתך\', \'וישכב במקום ההוא\'. \r\n<strong>...</strong>    (<a href=\"http://www.google.com/search?q=cache:Ay-DD-6NiLMJ:www.yba.org.il\">cache</a>)</li>   \r\n<li>\r\n<a href=\"/daat/tanach/raba1/68.htm\">פרשת ויצא</a>: רבי פנחס בשם רבי הונא בר פפא פתח: (משלי ג) אז תלך לבטח דרכך (וגו\') \r\n<strong>אם תשכב לא</strong>      \r\n<strong>תפחד</strong>. אז תלך לבטח, זה יעקב, דכתיב: ויצא יעקב. \r\n<strong>אם תשכב לא תפחד</strong>, מעשו ומלבן. \r\n<strong>...</strong>    (<a href=\"http://www.google.com/search?q=cache:DNAJJj11xkUJ:www.daat.ac.il\">cache</a>)</li>   \r\n<li>\r\n<a href=\"http://moreshet.co.il/web/shut/print.asp?id=8474&amp;kod=&amp;modul=15&amp;codeClient=289\">שו\"ת מורשת</a>: וכלשון שלמה המלך בפסוקים הבאים: \"ויהיו חיים לנפשך וחן לגרגרותיך, אז תלך לבטח   רגליך ורגליך לא תגוף, \r\n<strong>אם תשכב לא תפחד</strong>    ושכבת וערבה שנתך, אל תירא מפחד פתאום \r\n<strong>...</strong>    (<a href=\"http://www.google.com/search?q=cache:UgT7Kr7-ye0J:moreshet.co.il\">cache</a>)</li> \r\n<li>\r\n<a href=\"http://www.beitorot.org/content.asp?pageid=91\">מגייס את ערמתו ש צורך הוא כשי והעדין \" התמים \" למדנו שיעקב</a>: וכשמגיע רגע היציאה בדרך לחרן אומר המדרש: \"<strong>אם תשכב לא תפחד</strong>\"- מעשו. ולבן\" - אותו   עשו החפץ להורגו ולבן הארמי המצפה לו, שעליו נאמר בהמשך \"עם \r\n<strong>...</strong>    (<a href=\"http://www.google.com/search?q=cache:AHSeDow6KckJ:www.beitorot.org\">cache</a>)</li>   \r\n<li>\r\n<a href=\"http://www.yeshiva.org.il/midrash/doc/doc29/lv_tfiyk.doc\">השיעור ניתן ב-סיון ה\'תשס\"א</a>: \"ר\' פינחס בשם ר\' חנינא בר פפא פתח: אז תלך לבטח דרכך - זה יעקב דכתיב:ויצא יעקב,   \r\n<strong>אם תשכב לא תפחד</strong>    - מעשו ולבן, ושכבת וערבה שנתך - וישכב במקום ההוא\" (בר\"ר ס\"ח \r\n<strong>...</strong>    (<a href=\"http://www.google.com/search?q=cache:m3p7__qdF6IJ:www.yeshiva.org.il\">cache</a>)</li> \r\n<li>\r\n<a href=\"/tnk1/messages/sofrim_hagay_prjot_0_3.html\">הפסוק הראשון בפרשת תזריע - חשיבות טהרת המשפחה</a>: ונאמר, \r\n<strong>אם תשכב לא תפחד</strong>    ושכבת וערבה שנתך (משלי ג\'). בעולם הבא שנאמר, אני ה\' אלהיך   מלמדך להועיל מדריכך בדרך תלך, לוא הקשבת למצותי ויהי כנהר שלומך וצדקתך כגלי \r\n<strong>...</strong>    (<a href=\"http://www.google.com/search?q=cache:MQBzuYZOGwgJ:tora.us.fm\">cache</a>)</li>   \r\n<li>\r\n<a href=\"http://www.temani.net/http/mnagim/halacot/eislreho.htm\">ברכות איש לרעהו</a>: ושכבת וערבה שנתך. -תקיץ ברחמים (נ\"א בנעימים). \r\n<strong>אם תשכב לא תפחד</strong>. או יאמר לו תלין   בטוב. היה יושב בלילה עם חבירו ורצה לצאת מאצלו \r\n<strong>...</strong>    (<a href=\"http://www.google.com/search?q=cache:vZZW4i093GkJ:www.temani.net\">cache</a>)</li>   \r\n  \r\n<li>\r\n<a href=\"/daat/toshba/minhagim/sukot.htm\">סוכות והושענא-רבא</a>: אח\"כ קוראים יחד \"ברוך א-להינו שבראנו לכבודו\" וכו\' עד הסוף, הפסוקים: \"המלאך הגואל   אותי\" וגו\', \"שמע ישראל\", \"שמרני כאישון בית עין\" וגו\', \"<strong>אם תשכב לא תפחד\"...</strong>    (<a href=\"http://www.google.com/search?q=cache:ul_28TB8KS8J:www.daat.ac.il\">cache</a>)</li>   \r\n<li>\r\n<a href=\"http://www.fxp.co.il/showthread.php?t=821046\">תפלת רבי ישמעאל - FXP.co.il</a>: על כל עסקי לטובה, וכשאשכב לא אפחד, כדכתיב: \r\n<strong>אם תשכב לא תפחד</strong>, ושכבת וערבה שנתך.   ויהיו חיי מתוקים כדבש ונופת צופים, ותהיו עמי לעזור ולהועיל \r\n<strong>...</strong>    (<a href=\"http://www.google.com/search?q=cache:i4A__sLjxOQJ:www.fxp.co.il\">cache</a>)</li>   \r\n<li>\r\n<a href=\"http://www.dat.gov.il/%D7%A9%D7%99%D7%A8%D7%95%D7%AA%D7%99+%D7%93%D7%AA/%D7%A4%D7%98%D7%99%D7%A8%D7%94+%D7%95%D7%A7%D7%91%D7%95%D7%A8%D7%94/%D7%93%D7%99%D7%A0%D7%99+%D7%95%D7%9E%D7%A0%D7%94%D7%92%D7%99+%D7%90%D7%91%D7%9C%D7%95%D7%AA/%D7%A0%D7%95%D7%A1%D7%97+%D7%90%D7%96%D7%9B%D7%A8%D7%94/%D7%90%D7%96%D7%9B%D7%A8%D7%94+%D7%A0%D7%95%D7%A1%D7%97+%D7%90%D7%A9%D7%9B%D7%A0%D7%96+%D7%9C%D7%90%D7%99%D7%A9+%D7%90%D7%99%D7%A9%D7%94.htm\">אזכרה נוסח אשכנז לאיש/אישה</a>: וכתיב \r\n<strong>אם תשכב לא תפחד</strong>    ושכבת וערבה שנתך. ותשמור אותו (לנקבה אותה) מחבוט הקבר   ומרמה ותולעה ותסלח ותמחול לו על כל פשעיו (לנקבה לה על כל פשעיה) כי אדם אין צדיק   \r\n<strong>...</strong>    (<a href=\"http://www.google.com/search?q=cache:_D2lRZ6zEyEJ:www.dat.gov.il\">cache</a>)</li>   \r\n<li>\r\n<a href=\"http://www.nervashem.com/Information.aspx\">מידע לציבור</a>: וכתיב \r\n<strong>אם תשכב לא תפחד</strong>    ושכבת וערבה שנתך. ותשמור אותו (לנקבה אותה) מחבוט הקבר   ומרמה ותולעה. ותסלח ותמחול לו על כל פשעיו (לנקבה לה על כל פשעיה) כי אדם אין צדיק   \r\n<strong>...</strong>    (<a href=\"http://www.google.com/search?q=cache:m0VCoPUwk3MJ:www.nervashem.com\">cache</a>)</li>   \r\n<li>\r\n<a href=\"http://www.tsel.org/torah/midrashraba/vayetse.html\">מדרש רבה - ויצא</a>: פרשה סח א ויצא יעקב מבאר שבע וילך חרנה רבי פנחס בשם רבי הונא בר פפא פתח (משלי ג)   אז תלך לבטח דרכך (וגו\') \r\n<strong>אם תשכב לא תפחד</strong>    אז תלך לבטח זה יעקב דכתיב ויצא יעקב \r\n<strong>...</strong>    (<a href=\"http://www.google.com/search?q=cache:JnImUIaD75QJ:www.tsel.org\">cache</a>)</li>   \r\n<li>\r\n<a href=\"http://www.tsel.org/torah/yalkutsh/mishli.html\">ילקוט שמעוני - משלי</a>: \r\n<strong>אם תשכב לא תפחד</strong>    מעשו ומלבן. ושכבת וערבה שנתך וישכב במקום ההוא. ר\' הונא אומר תורת   אמת היתה בפיהו אלו דברים ששמע מרבו. ועולה לא נמצא בשפתיו אלו דברים שלא שמע \r\n<strong>...</strong>    (<a href=\"http://www.google.com/search?q=cache:WN9g3mBF6TUJ:www.tsel.org\">cache</a>)</li>   \r\n<li>\r\n<a href=\"http://www.tsel.org/torah/yalkutsh/vayetse.html\">ילקוט שמעוני - ויצא</a>: \r\n<strong>אם תשכב לא תפחד</strong>    מלבן ומעשו ושכבת וערבה שנתך וישכב במקום ההוא. רבי שמואל בר נחמן   פתח אשא עיני אל ההרים אל ההורים למלפני ולמעבדני. \r\n<strong>...</strong>    (<a href=\"http://www.google.com/search?q=cache:r72lVEPQZdYJ:www.tsel.org\">cache</a>)</li></ul> \r\n		</div>   \r\n		\r\n  \r\n' WHERE `book` = 'משל' AND `chapter_number` = 3 AND `verse_number` = 24 AND 1 LIMIT 1;

