UPDATE `sgulot` SET `mqorot` = '<p>בנמשל: הציפור היא הנער, והציידים הם החַטָּאִים \r\nהמנסים לפתות אותו לבוא איתם. \"הם מציעים לך להצטרף אליהם ולהרוויח הרבה \r\nכסף בחינם, אבל זה רק פתיון! למעשה המטרה שלהם היא ללכוד אותך ולהשתמש בך, \r\nואז להרוג אותך כשכבר לא יהיה בך צורך\".</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 1 AND `verse_number` = 18 AND 1 LIMIT 1;

