UPDATE `sgulot` SET `mcudot` = '<p><strong>תחת </strong>(בעבור אשר) <strong>שנאו דעת.</strong></p>\r\n\r\n\r\n\r\n\r\n\r\n', `tirgum` = '<p>- האסון יבוא על הכסילים <strong>תחת כי</strong> (כעונש על כך ש-)&#160; פיתחו רגשות של\r\n<strong>\r\nשנאה </strong>ללימודי <strong>דעת</strong> ה\', ולא פיתחו רגשות של <strong>יראת </strong>כבוד כלפי <strong>ה\'</strong>;&#160;&#160;&#160; <strong>כי</strong> הם \r\n<strong>\r\nשנאו</strong> <strong>לדעת</strong> את העובדות, ו<strong>לא בחרו</strong> להיות <strong>יראים</strong> וזהירים ולחשוש מסכנות;</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 1 AND `verse_number` = 29 AND 1 LIMIT 1;

