UPDATE `sgulot` SET `hqblot` = '<p>         בפסוק שלנו, הבינה קודמת ליראת ה\'; אך בפסוקים אחרים, לכאורה, יראת ה\' קודמת לחכמה ולדעת. לדיון מפורט בנושא ראו בביאורנו על <a class=\"psuq\" href=\"/tnk1/prqim/t2801.htm#7\">משלי א7</a>: \"<q class=\"psuq\">יִרְאַת ה\' רֵאשִׁית דָּעַת חָכְמָה וּמוּסָר אֱוִילִים בָּזוּ</q>\" (<a href=\"/tnk1/ktuv/mj/01-07.html\">פירוט</a>).</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 2 AND `verse_number` = 5 AND 1 LIMIT 1;

