UPDATE `sgulot` SET `tirgum` = '<p>\r\n<strong>הבוצע בצע</strong> ולוקח את רכוש הזולת בלי רשותו, <strong>עוכר</strong>   (מקלקל ומשחית) את  <strong>ביתו</strong>, רכושו ומשפחתו; &#160; אולם <strong>השונא</strong>   לקבל את רכוש הזולת גם ברשותו, כ<strong>מתנות</strong>,  <strong>יחיה</strong> חיים עצמאיים ושלווים.</p>' WHERE `book` = 'משל' AND `chapter_number` = 15 AND `verse_number` = 27 AND 1 LIMIT 1;

