UPDATE `sgulot` SET `tirgum` = '<p>- התבונה <strong>תציל</strong> אות<strong>ך</strong> <strong>מדרכ</strong>ו של איש <strong>רע</strong>, וגם <strong>מאיש המהפך</strong> ומערבב <strong>בדיבורו</strong> בין טוב לרע.</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 2 AND `verse_number` = 12 AND 1 LIMIT 1;

