UPDATE `sgulot` SET `tirgum` = '<p>-- התבונה תציל אותך גם מה<strong>עוזבים </strong>את <strong>אורחות </strong>ההתנהגות <strong>הישרות</strong> והמקובלות בין אנשים הגונים, <strong>והולכים בדרכים</strong> <strong>חשוכות</strong> נסתרות ולא צפויות;</p>\r\n  \r\n', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 2 AND `verse_number` = 13 AND 1 LIMIT 1;

