UPDATE `sgulot` SET `tirgum` = '<p>-- התבונה תציל אותך גם מאנשים חסרי-מצפון, ה<strong>שמחים </strong>על \r\nכל הזדמנות שיש להם <strong>לעשות רע</strong> ולפגוע בזולת, ונהנים <strong>להפוך </strong>את המעשה ה<strong>רע</strong> שלהם ולהציגו כמעשה טוב.</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 2 AND `verse_number` = 14 AND 1 LIMIT 1;

