UPDATE `sgulot` SET `verse_text` = 'אֲשֶׁר אָרְחֹתֵיהֶם עִקְּשִׁים,\r\nוּנְלוֹזִים בְּמַעְגְּלוֹתָם.', `mcudot` = '<p><strong>אשר ארחותיהם עקשים</strong> (עקומים), <strong>ונלוזים</strong> (נוטים מדרך הישר) <strong>ב</strong>כל <strong>מעגלותם</strong> - אין סברה ישרה בכל דרכי למודם.</p>', `mqorot` = '<p>הפסוק מתייחס שוב לשני סוגי האנשים הרעים:</p><p><strong>עיקש</strong>   \r\n<a href=\"http://localhost/tnk1/kma/qjrim1/iqj.html\">= עקום וקשה</a>; &#160; \r\n<strong>אשר אורחותיהם עיקשים</strong>   = האנשים הרעים הולכים בדרכים עקומות וקשות, אלימות ולא הוגנות.\r\n</p>\r\n  \r\n<p>\r\n<strong>נלוז</strong>   \r\n<a href=\"http://localhost/tnk1/kma/qjrim1/lz.html\">= עקום ונסתר</a>;&#160;&#160; \r\n<strong>ונלוזים במעגלותם</strong>   = אנשי התהפוכות הולכים בדרכים עקומות ונסתרות. </p><p>לסיכום: הנוכלים הולכים בדרכים עקומות ולא צפויות; אל תחשבו שאתם מבינים אותם או שאתם יכולים לחזות איך הם יתנהגו; פשוט תתרחקו מהם! \r\n</p>', `ecot` = '<p>הפסוק מתייחס שוב לשני סוגי האנשים הרעים:</p><p><strong>עיקש</strong>   \r\n<a href=\"/tnk1/kma/qjrim1/iqj.html\">= עקום וקשה</a>; &#160; \r\n<strong>אשר אורחותיהם עיקשים</strong>   = האנשים הרעים הולכים בדרכים עקומות וקשות, אלימות ולא הוגנות.\r\n</p>\r\n  \r\n<p>\r\n<strong>נלוז</strong>   \r\n<a href=\"/tnk1/kma/qjrim1/lz.html\">= עקום ונסתר</a>;&#160;&#160; \r\n<strong>ונלוזים במעגלותם</strong>   = אנשי התהפוכות הולכים בדרכים עקומות ונסתרות. </p><p>לסיכום, הנוכלים הולכים בדרכים עקומות ולא צפויות; אל תחשבו שאתם מבינים אותם או שאתם יכולים לחזות איך הם יתנהגו; פשוט תתרחקו מהם! \r\n</p>', `hqblot` = '<p class=\"future\"> ישנ<span class=\"future\"></span>ו פסוק נוסף העוסק בתכונות העיקש והנלוז, \r\n<a class=\"psuq\" href=\"/tnk1/prqim/t2804.htm#24\">משלי ד24</a>: \"<q class=\"psuq\">הָסֵר מִמֶּךָּ \r\nעִקְּשׁוּת פֶּה, \r\nוּלְזוּת שְׂפָתַיִם הַרְחֵק מִמְּךָ</q>\" (<a href=\"/tnk1/ktuv/mj/04-24.html\">פירוט</a>).\r\n</p>', `tosfot` = NULL, `full` = '<p><a href=\"/tnk1/ktuv/mj/02-12.html\">המאמר המקורי - ב12</a></p>\r\n' WHERE `book` = 'משל' AND `chapter_number` = 2 AND `verse_number` = 15 AND 1 LIMIT 1;

