UPDATE `sgulot` SET `hqblot` = '<p><strong>מזימה</strong> במשמעות חיובית נזכרה גם ב<a class=\"psuq\" href=\"http://tora.us.fm/tnk1/prqim/t2801.htm#4\">משלי א4</a>: \"<q class=\"psuq\">לָתֵת לִפְתָאיִם עָרְמָה, לְנַעַר <strong>דַּעַת וּמְזִמָּה</strong></q>\" (<a href=\"http://tora.us.fm/tnk1/ktuv/mj/01-04.html\">פירוט</a>).</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 2 AND `verse_number` = 11 AND 1 LIMIT 1;

