UPDATE `sgulot` SET `mcudot` = '<p><strong>יען</strong> (לכן, הואיל ו)<strong>מאנתם</strong> לשמוע מה ש<strong>קראתי</strong>,&#160;&#160;&#160; כדרך אדם המרמז <strong>בנטיית היד</strong> להקשיב מאמריו, <strong>ואין</strong> מי <strong>מקשיב</strong>,</p>\r\n\r\n\r\n\r\n\r\n\r\n', `tirgum` = '<p>לאחר זמן, אומרת החכמה: \"מפני ש<strong>קראתי</strong> אליכם לשוב וללמוד חכמה, ואתם <strong>מיאנתם</strong> (סירבתם לבוא); &#160; ואחר-כך התרחקתם עוד יותר, ואני <strong>נטיתי ידי</strong>   ונפנפתי לכם לחזור, אולם ראיתי <strong>שאין</strong> בכם אף אחד ש<strong>מקשיב</strong> -\"<a class=\"psuq\" href=\"../../prqim/t2801.htm#25\">\r\n</a></p>\r\n', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 1 AND `verse_number` = 24 AND 1 LIMIT 1;

