UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '\r\n\r\n<p>- <strong>כי בית</strong> הזונה הוא מקום מדרון,&#160;לרדת בה <strong>אל המיתה</strong>;&#160;&#160;&#160; <strong>ו</strong>ההולכים אל ביתה הולכים המה <strong>אל</strong> מקום ה<strong>רפאים</strong>, הם המתים שנרפו ונחלשו וכוחם תש.</p><p>ורוצה לומר: הבא אל ביתה יוכשל בה ויאבד נפשו.\r\n\r\n\r\n\r\n</p>', `tirgum` = '<p>-- <strong>כי </strong>האשה הזרה <strong>שחה </strong>(מכופפת ומשפילה) <strong>אל המוות </strong>את <strong>ביתה</strong>, משפחתה וכל מי שמתקרב אליה;&#160;&#160; והאשה הנכריה משפילה אל <strong>רפאים</strong> (חלשים נרפים וחסרי שורשים) בכל <strong>מעגלותיה </strong>ודרכי התנהגותה.</p>', `mqorot` = '<h3> שחה אל מוות ביתה</h3><p>מה משמעות הפועל <strong>שחה</strong> ומי הנושא שלו?</p><p>1. <strong>שחה</strong> = \"<q class=\"mfrj\">ענין כפיפה והשפלה</q>\"<small class=\"small\"> (מצודת ציון)</small>, כמו ב<a class=\"psuq\" href=\"http://localhost/tnk1/prqim/t1002.htm#9\">ישעיהו ב9</a>,&#160;<a class=\"psuq\" href=\"http://localhost/tnk1/prqim/t1005.htm#15\">ישעיהו ה15</a>, <a class=\"psuq\" href=\"http://localhost/tnk1/prqim/t26a7.htm#39\">תהלים קז39</a>. &#160; הנושא הוא ביתה של האשה הזרה - המתכופף ויורד אל המוות (<strong>שחה</strong> - פועל עומד, לשון זכר), או האשה הזרה עצמה, המורידה את ביתה, ואת כל מי שבא אל ביתה, אל המוות (<strong>שחה</strong> - פועל יוצא, לשון נקבה).</p><p>2. <strong>שחה</strong> = כמו <strong>חשה</strong>, והנושא הוא האשה הזרה, המגיעה במהירות אל המוות - שהוא ביתה <small class=\"small\"> (דעת מקרא, וראו שם פירושים נוספים בהערת שוליים)</small>.</p><h3>ואל רפאים מעגלותיה</h3><p><strong>רפאים</strong>    \r\n  <a href=\"http://localhost/tnk1/kma/qjrim1/rfaim.html\">= מתים, או אנשים חלשים ורפויים</a>;&#160;&#160; <strong>מעגל</strong>     \r\n  <a href=\"http://localhost/tnk1/kma/qjrim1/magl.html\">= אופן-התנהגות קבוע ומחזורי</a>;&#160;&#160;&#160; האשה הנואפת מביאה את כל מי שהולך במעגלותיה, כל מי שנכנס לחוג החברתי שלה, לחולשה ורפיון רוחניים, עד מוות.</p>', `dquyot` = '<p><strong>שחה</strong> - מה משמעות הפועל, ומי הנושא שלו?</p><p>1. <strong>שחה</strong> = \"<q class=\"mfrj\">ענין כפיפה והשפלה</q>\"<small class=\"small\"> (מצודת ציון)</small>, כמו ב<a class=\"psuq\" href=\"/tnk1/prqim/t1002.htm#9\">ישעיהו ב9</a>: \"<q class=\"psuq\"><strong>וַיִּשַּׁח</strong> אָדָם וַיִּשְׁפַּל אִישׁ וְאַל תִּשָּׂא לָהֶם</q>\", <a class=\"psuq\" href=\"/tnk1/prqim/t1005.htm#15\">ישעיהו ה15</a>: \"<q class=\"psuq\"><strong>וַיִּשַּׁח</strong> אָדָם וַיִּשְׁפַּל אִישׁ וְעֵינֵי גְבֹהִים תִּשְׁפַּלְנָה</q>\", <a class=\"psuq\" href=\"/tnk1/prqim/t26a7.htm#39\">תהלים קז39</a>: \"<q class=\"psuq\">וַיִּמְעֲטוּ <strong>וַיָּשֹׁחוּ</strong> מֵעֹצֶר רָעָה וְיָגוֹן</q>\".</p><p>הנושא הוא ביתה של האשה הזרה - המתכופף ויורד אל המוות (<strong>שחה</strong> - פועל עומד, לשון זכר), או האשה הזרה עצמה, המורידה את ביתה, ואת כל מי שבא אל ביתה, אל המוות (<strong>שחה</strong> - פועל יוצא, לשון נקבה).</p><p>2. <strong>שחה</strong> = כמו <strong>חשה</strong>, והנושא הוא האשה הזרה, המגיעה במהירות אל המוות - שהוא ביתה <small class=\"small\"> (דעת מקרא, וראו שם פירושים נוספים בהערת שוליים)</small>.</p><p><strong>ואל רפאים מעגלותיה</strong> -</p><p><strong>רפאים</strong>    \r\n  <a href=\"/tnk1/kma/qjrim1/rfaim.html\">= מתים, או אנשים חלשים ורפויים</a>;&#160;&#160; <strong>מעגל</strong>     \r\n  <a href=\"/tnk1/kma/qjrim1/magl.html\">= אופן-התנהגות קבוע ומחזורי</a>;&#160;&#160;&#160; האשה הנואפת מביאה את כל מי שהולך במעגלותיה, כל מי שנכנס לחוג החברתי שלה, לחולשה ורפיון רוחניים, עד מוות.</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 2 AND `verse_number` = 18 AND 1 LIMIT 1;

