UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '\r\n\r\n<p>יותר <strong>טוב</strong> למי שנוהג <strong>קלות</strong> בעצמו והוא <strong>עבד לעצמו</strong> לעשות מלאכתו להתפרנס בה,&#160;&#160;&#160; ממי שמחזיק עצמו <strong>למכובד</strong> ובוש הוא לעבוד עבודתו ובעבור זה <strong>יחסר לחמו</strong>.\r\n\r\n\r\n\r\n</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 12 AND `verse_number` = 9 AND 1 LIMIT 1;

