UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '\r\n\r\n<p><strong>הצדיק</strong> נותן לב <strong>לדעת</strong> אף רצון <strong>בהמתו</strong> למלאות תאותה, כי קנוי אצלו מדת הרחמנות;&#160;&#160; אבל <strong>הרשעים</strong>, אף <strong>הרחמנות</strong> שיש בהם היא <strong>אכזריות</strong>, כי למראה העין ירחמו ולא כן בלבם.\r\n\r\n\r\n\r\n</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 12 AND `verse_number` = 10 AND 1 LIMIT 1;

