UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '<p><strong>הצדיק</strong>, יש לו <strong>יתרון</strong> ומעלה <strong>מרעהו</strong> שאינו צדיק;&#160;&#160;&#160; אבל <strong>דרך רשעים</strong> המצלחת היא <strong>מתעה</strong> את הרשעים לאחוז בדרכם, ולא יפנו אל היתרון ההוא.</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n', `main_qod_subjects` = NULL, `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 12 AND `verse_number` = 26 AND 1 LIMIT 1;

