UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '<p>דרך <strong>הצייד לחרוך</strong> (לשרוף) ראשי כנפי העופות לבל יעופו ממנו; אבל <strong>\r\nהרמאי</strong> <strong>לא יחרכם</strong>, על כי המה נראים יפים כאשר כנפיהם שלמים, ונוח לרמות את \r\nהלוקחים, ובעבור זה יאבד הכל כי יעופו להם; &#160; והוא למשל על הקופץ יד מלתת מה \r\nמהונו לצדקה, כי יפסיד את כל ההון.</p><p>אבל ה<strong>הון</strong> של <strong>אדם יקר</strong> (חשוב ונכבד), המפריש מהונו לדל, ההון ההוא הוא <strong>חרוץ</strong>, ר\"ל מובחר שבזהבים, המתקיים לזמן מרובה, ולא יפסד כשאר מיני מתכות; ר\"ל: עושרו מתקיים.</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 12 AND `verse_number` = 27 AND 1 LIMIT 1;

