UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '<p><strong>ודרך</strong> - ב<strong>דרך נתיב</strong> הצדקה לא ימצא את ה<strong>מוות</strong>; וכפל הדבר במלות שונות.</p>\r\n<p><strong>באורח</strong> - ההולך <strong>בדרך צדקה</strong> ימצא <strong>חיים</strong>.</p>\r\n<h3><span class=\"mw-headline\" id=\".D7.9E.D7.A6.D7.95.D7.93.D7.AA_.D7.A6.D7.99.D7.95.D7.9F\">מצודת ציון</span></h3>\r\n<p><strong>נתיבה</strong> - ענין שביל ומסילה</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n', `main_qod_subjects` = NULL, `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 12 AND `verse_number` = 28 AND 1 LIMIT 1;

