UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '<p><strong>הרשע חומד</strong> לשבת ב<strong>מצוד</strong> (מבצר) של <strong>הרעים</strong> אשר יתחזקו שם לעשות חמס;&#160;&#160; אבל <strong>הצדיקים</strong> אינם צריכים לעזר מבחוץ, כי <strong>שרשם יתן</strong> פריים הראוי, ר\"ל: מעצמם יתחזקו להיטיב לעשות.</p>', `tirgum` = '<p><strong>הרשע חומד</strong> (מתאווה) להרוויח בקלות על חשבון אחרים, ולכן הוא עוסק <strong>בציד</strong>, הרג וגזל, יחד עם חבריו <strong>הרעים</strong>;&#160;&#160;&#160; אולם <strong>הצדיקים</strong> מחוברים לקרקע כמו עץ עם <strong>שורש</strong> חזק, והשורש הזה <strong>נותן</strong> להם פרנסה מעבודת אדמתם.</p>', `main_qod_subjects` = NULL, `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 12 AND `verse_number` = 12 AND 1 LIMIT 1;

