UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '\r\n\r\n<p><strong>הצדיק שונא</strong> לקבל <strong>דבר שקר</strong> ולשון הרע;&#160;&#160;&#160; אבל <strong>הרשע</strong> מקבל הוא מייד, וימאס את הנאמר עליו כדבר ה<strong>נבאש</strong>, <strong>ויחפיר</strong> (יבייש) אותו.\r\n\r\n\r\n\r\n</p>', `tirgum` = ' \r\n<p>\r\n<strong>הצדיק</strong> <strong> שונא דברי-שקר</strong>, המעוותים את המציאות ומונעים עשיית צדק;&#160;&#160; אולם <strong>הרשע</strong>  מוכן \r\n<strong>להבאיש ולהחפיר</strong>  את יריבו בדברי-שקר כדי לנצח במשפט.</p><p>/ <strong>הצדיק שונא דברי-שקר</strong>, ולכן הוא חושף את שקריו של ה<strong>רשע</strong> ברבים ומביא על הרשע <strong>בושה וחרפה</strong>.</p>', `mqorot` = '<h3>\r\nמי <strong>מבאיש</strong> את מי?</h3><p>    \r\n   1. \r\n<strong>הרשע</strong>  גורם בושה וחרפה לאחרים \r\n<small>(כך פירשו רוב המפרשים)</small>.\r\n</p><p>2. \r\n<strong>הרשע</strong>  מבאיש ומחפיר את עצמו (יבאיש, יחפיר - פעלים עומדים) במעשיו הנבזים \r\n<small>(דעת מקרא)</small>.\r\n<br />\r\n</p><p>3. \r\n<strong>הצדיק</strong>  גורם בושה וחרפה לרשע, בכך שהוא חושף את שקריו \r\n<small>(מלבי\"ם פירוש שני)</small>.</p> \r\n<p>הצדיק שונא את השקרים, אבל לא את השקרנים; ראו \r\n<a href=\"http://localhost/tnk1/kma/qjrim1/sna.html\">שנאת רע או רעים?</a>.</p>', `hqblot` = '<p>רעיון דומה ב<a class=\"psuq\" href=\"/tnk1/prqim/t2826.htm#28\">משלי כו28</a>: \"<q class=\"psuq\">לְשׁוֹן <strong>שֶׁקֶר יִשְׂנָא </strong>דַכָּיו, וּפֶה חָלָק יַעֲשֶׂה מִדְחֶה</q>\" (<a href=\"/tnk1/ktuv/mj/26-28.html\">פירוט</a>).</p><p>הצדיק שונא את השקרים, אבל לא את השקרנים; ראו: \r\n<a href=\"http://localhost/tnk1/kma/qjrim1/sna.html\">שנאת רע או רעים?</a>.</p>', `dquyot` = '<h3>מי <strong>מבאיש</strong> את מי?</h3><p>    \r\n   1. \r\n<strong>הרשע</strong>  גורם בושה וחרפה לאחרים \r\n<small>(ר\' יונה, רש\"י, מצודות ועוד)</small>. </p><p>- אולם לפי זה, הניגוד בין חצאי הפסוק אינו ברור: החצי הראשון מדבר על שקר והחצי השני מדבר על בושה. היה ראוי לכתוב: \"דבר שקר ישנא צדיק, ורשע <strong>ירמה וישקר</strong>\", או \"דבר <strong>חרפה</strong> ישנא צדיק, ורשע יבאיש ויחפיר\".</p><p>2. \r\n<strong>הרשע</strong>  מבאיש ומחפיר את עצמו (יבאיש, יחפיר - פעלים עומדים) בשקריו ובמעשיו הנבזים \r\n<small>(דעת מקרא)</small>.&#160;</p><p>- אולם גם לפי זה הניגוד בין חצאי הפסוק אינו ברור.<br />\r\n</p><p>3. \r\n<strong>הצדיק</strong>  גורם בושה וחרפה לרשע, בכך שהוא חושף את שקריו \r\n<small>(מלבי\"ם פירוש שני)</small>.</p> \r\n', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 13 AND `verse_number` = 5 AND 1 LIMIT 1;

SET character_set_client=utf8;
SET character_set_connection=utf8;
UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '\r\n\r\n<p>ה<strong>צדקה</strong> עצמה תלמד זכות ו<strong>תצור</strong> את ה<strong>תם דרך</strong>, זה הנותן צדקה בתמימות לא להתיהר;&#160;&#160;&#160; <strong>ו</strong>ה<strong>רשעה</strong> עצמה תוליך את בעל <strong>החטאת</strong> בדרך <strong>מסולפת</strong> (מעוקלת ומעוקמת) להכשל בה.\r\n\r\n</p>', `tirgum` = '<p>דרך \r\n<strong>הצדק</strong>  היא \r\n<strong>לנצור</strong>  (לשמור) על האנשים הנוהגים \r\n<strong>בתום-דרך</strong>, ולא לפגוע בחפים מפשע;&#160;&#160; אולם דרך \r\n<strong>הרשע</strong>  היא \r\n<strong>לסלף</strong>  כל \r\n<strong>חטא</strong>  שוגג של הזולת ולהציג אותו כזדון, כדי להחמיר בעונשם.</p>', `mqorot` = '<p>1. \r\n<strong>צדקה</strong>        היא דרכו של הצדיק (<a href=\"http://localhost/tnk1/kma/qjrim1/cdiq.html\">= הנותן לכל אחד את שמגיע\r\nלו</a>);&#160;&#160; \r\n<strong>צדקה תצור תם דרך</strong>     = דרכו של\r\nהצדיק    היא, \r\nלשמור    ולהגן על אנשים \r\nתמימי דרך    (<a href=\"http://localhost/tnk1/kma/qjrim1/tm.html\">= שלמים ללא פגם במעשיהם</a>).</p> \r\n<p>\r\n<strong>רשעה</strong>        היא דרכו של הרשע (<a href=\"http://localhost/tnk1/kma/qjrim1/rja.html\">= העושה רע בשפע</a>); &#160; \r\n<strong>ורשעה תסלף חטאת</strong>     = דרכו של\r\nהרשע    היא, \r\nלסלף    ולעקם את דרכם של האנשים \r\nהחוטאים    (<a href=\"http://localhost/tnk1/kma/qjrim1/xte.html\">= העוברים עבירות בשוגג או\r\nמתוך חוסר-איפוק</a>) כלומר - להציג אותם כעבריינים גדולים יותר ממה\r\nשהם באמת (למשל על-מנת\r\nלהפליל אותם במשפט כאשר הם נמצאים בצד השני), או - להסית אותם ולגרום להם\r\nלהיות עבריינים גדולים יותר, או להיפך - לעקם ולעוות את החטא שלהם כך\r\nשייראה כמעשה טוב.</p>      \r\n   \r\n<p>והפסוק קשור לפסוק הקודם,&#160;\r\n<a class=\"psuq\" href=\"http://localhost/tnk1/prqim/t2813.htm\">\r\nמשלי יג5</a>: \"<q class=\"psuq\">דְּבַר שֶׁקֶר יִשְׂנָא צַדִּיק, וְרָשָׁע יַבְאִישׁ וְיַחְפִּיר</q>\": הצדיק שונא דברי-שקר כי הם פוגעים בחפים\r\nמפשע, והרשע אוהב להגיד דברים רעים הגורמים בושה\r\nוחרפה ליריביו (<a href=\"http://localhost/tnk1/ktuv/mj/13-05.html\">פירוט</a>).\r\n</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 13 AND `verse_number` = 6 AND 1 LIMIT 1;

