UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '<p><strong>המבזה דבר</strong> מה ממצוות ה\', הוא <strong>חובל</strong> (מַכֵּה) בעצמו;&#160;&#160; אבל <strong>הירא</strong> מן <strong>המצוה</strong> ומגדל מעלתה, <strong>ישולם</strong> לו שכר גם בעבור זה.</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 13 AND `verse_number` = 13 AND 1 LIMIT 1;

