UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '\r\n\r\n<p><strong>שכל טוב</strong> - <strong>השכל הטוב</strong> שיש לאדם הוא <strong>יתן</strong> אותו ל<strong>חן</strong> בעיני כל; ועם-כל-זה, <strong>דרך הבוגדים</strong> הוא חזק מאד, ולא יועיל בעל השכל עם שכלו ורב חנו להסירם מדרך ההוא.</p>\r\n<h3><span class=\"mw-headline\" id=\".D7.9E.D7.A6.D7.95.D7.93.D7.AA_.D7.A6.D7.99.D7.95.D7.9F\">מצודת ציון</span></h3>\r\n<p><strong>איתן</strong> - ענין חוזק, כמו ():</span> <strong>איתן מושבך</strong>. משלי</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n', `mqorot` = '<br />', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 13 AND `verse_number` = 15 AND 1 LIMIT 1;

