UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '\r\n<p><strong>תורת חכם </strong>היא כ<strong>מקור</strong> הנובע <strong>חיים</strong>, כי תלמדו <strong>לסור מהמוקשים</strong> המביאים את <strong>המיתה</strong>, והם העוונות.</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 13 AND `verse_number` = 14 AND 1 LIMIT 1;

