SET character_set_connection=utf8;
UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '\r\n<p><strong>כל</strong> <strong>מעשה</strong> ה<strong>ערום</strong> המה <strong>בדעת</strong> וכוונה מיוחדת, ואם כי לא יבינו כל;&#160;&#160; אבל ה<strong>כסיל פורש</strong> (שוטח)<strong> אולתו</strong> למען יכירו הכל כי אולת הוא.</p>', `tirgum` = '<p>אדם \r\n<strong>ערום</strong>  (פיקח וזהיר) \r\n<strong>עושה</strong>  את \r\n<strong>כל</strong>  מעשיו בצורה ממוקדת, מתוך \r\n<strong>מודעות</strong>  והיכרות קרובה ומעמיקה; &#160; אולם אדם \r\n<strong>כסיל פורש</strong> (מפזר) את מעשיו על שטח נרחב ועושה אותם ב<strong>איוולת</strong>  (שטחיות).</p>', `mqorot` = '<p>ה<strong>דעת</strong> <a href=\"http://localhost/tnk1/kma/qjrim1/dat.html\">היא תשומת לב והיכרות קרובה</a>, והיא ניגודה של ה<strong>איוולת</strong> <a href=\"http://localhost/tnk1/kma/qjrim1/ewil.html\">- השטחיות</a>.</p><p><a href=\"http://localhost/tnk1/kma/qjrim1/dat.html\"></a></p>', `hqblot` = '<p>רוב המפרשים פירשו את הפסוק באופן כללי - הערום (הפיקח) יודע מה הוא עושה, והכסיל נוהג בשטחיות: \"<q class=\"mfrj\"><strong>כל ערום יעשה בדעת</strong>         - לזה ישיג המבוקש, ואולם\r\n<strong>הכסיל יפרוש אולתו</strong>         על כל דרך אשר </q><q class=\"mfrj\">ילך בה, בא</q>  \r\n \r\n<q class=\"mfrj\">ופן שלא ילך בשום חלק מהדרך לפי הראוי, ולזה ימנעו ממנו מבוקשיו</q>\" \r\n<small>(רלב\"ג)</small>.\r\n</p><p>אולם רש\"י פירש את הפסוק על דוד לעומת אחשורוש: \"<q class=\"mfrj\"><strong>כל ערום יעשה בדעת</strong>        \r\n- מעשהו, כגון דוד: \'יבקשו לאדוני המלך נערה בתולה...\' (מלכים א א):\r\n<strong>וכסיל יפרוש אולת</strong>        \r\n- זה אחשורוש: \'ויפקד המלך...\' (אסתר ב); יודעים היו שלא ישא לכולם, ומי שהיה לו בת הטמינה</q>\".\r\n</p> \r\n<p>עבדי דוד ועבדי אחשורוש רצו להשיג אותה מטרה - למצוא אישה חדשה עבור\r\nהמלך שלהם; אבל עבדי דוד ייעצו לו לעשות את החיפוש בצורה ממוקדת - לעבור\r\nבשקט ובצנעה בארץ ישראל ולחפש אישה מתאימה; בעוד שעבדי אחשורוש ייעצו לו\r\n\"לפרוש רשת\" - להזמין את כל הנשים היפות בממלכה ל\"מבחנים\" בארמון (ומעניין שהפועל \"פרש\" מזכיר את שם הממלכה \"פרס\").\r\n</p> \r\n<p>במושגים של ימינו היינו אומרים, שעבדי דוד נקטו ב\"פרסום ממוקד\", ועבדי אחשורוש השתמשו ב\"פרסום המוני\".</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 13 AND `verse_number` = 16 AND 1 LIMIT 1;

