UPDATE `sgulot` SET `prefix` = NULL, `tirgum` = '<p> דברים הנאמרים \r\n<strong>בשכל טוב</strong>, דברי-טעם המותאמים אישית לערכים החשובים לשומע, \r\n<strong>ייתנו</strong>   לדובר \r\n<strong>חן</strong> בעיני השומע וישכנעו אותו; </p><p>אולם ה<strong>דרך</strong> של ה<strong>בוגדים</strong> לשכנע את הזולת היא בכוח \r\n<strong>איתן</strong>        ובאלימות, כי אינם יודעים לדבר בשכל והגיון.</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 13 AND `verse_number` = 15 AND 1 LIMIT 1;

