UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '<p><strong>הולך את חכמים יחכם</strong> - כי ילמד מהם; &#160;&#160; והמחבר עצמו ל<strong>כסילים</strong> להיות לו ל<strong>רֵעים</strong>, <strong>ירוע</strong> (ישובר וירוצץ) עמהם, כי ילמוד מהם וגמולו ישיב לו.\r\n\r\n\r\n\r\n</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 13 AND `verse_number` = 20 AND 1 LIMIT 1;

