UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '<p>ה<strong>רעה</strong> שעושים ה<strong>חטאים</strong> היא <strong>תרדף</strong> אותם עד השמדם, כי תקטרג עליהם;&#160;&#160; <strong>ו</strong>הזכות ש<strong>את</strong> (עם) ה<strong>צדיקים</strong> הוא <strong>ישלם</strong> להם שכר <strong>טוב</strong>, כי יליץ טובה עליהם.</p>\r\n', `main_qod_subjects` = NULL, `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 13 AND `verse_number` = 21 AND 1 LIMIT 1;

