UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '\r\n<p>איש <strong>טוב</strong> <strong>ינחיל</strong> נחלתו אף ל<strong>בני בניו</strong>;&#160;&#160; אבל <strong>חיל</strong> (עושר) ה<strong>חוטא</strong> הוא <strong>צפון לצדיק</strong>, ואפילו לבניו לא ינחיל.\r\n\r\n</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 13 AND `verse_number` = 22 AND 1 LIMIT 1;

