SET character_set_connection=utf8;
UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '<p><strong>חושך</strong> - המונע מבנו <strong>שבט</strong> מוסר, הנה <strong>לשנאה</strong> תחשב, כי סופו יצא לתרבות רעה וימות בעוונו.</p>\r\n<p><strong>ואוהבו</strong> - <strong>האוהב</strong> את בנו, <strong>מייסרו</strong> בעת ה<strong>שחר</strong>, רוצה לומר: בילדותו, עת יוכל לנטותו לכל אשר יחפוץ.</p>\r\n<h3><span class=\"mw-headline\" id=\".D7.9E.D7.A6.D7.95.D7.93.D7.AA_.D7.A6.D7.99.D7.95.D7.9F\">מצודת ציון</span></h3>\r\n<p><strong>חושך</strong> - מונע.</p>\r\n<p><strong>שחרו</strong> - ענינו ימי הילדות, שהוא בתחילת הימים, והושאל מלשון שחר הבקר; וכן (): <strong>הילדות והשחרות הבל</strong>.</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 13 AND `verse_number` = 24 AND 1 LIMIT 1;

SET character_set_client=utf8;
SET character_set_connection=utf8;
UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '<p><strong>לשובע</strong> - אינו חפץ במעדנים ורק בדבר <strong>המשביע</strong> את <strong>הנפש</strong>; אבל <strong>הרשעים</strong> יתענגו באכילת מעדנים מתוקים וערבים, ותמיד <strong>בטנו חסרה</strong>, כי רווחא לבסימא שכיחא ויוכל עוד למלאותה.</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n', `main_qod_subjects` = NULL WHERE `book` = 'משל' AND `chapter_number` = 13 AND `verse_number` = 25 AND 1 LIMIT 1;

