UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '<div style=\"background:#eef\">\r\n<p> -  - פסוק     <strong class=\"selflink\">ה</strong>                              </p>\r\n</div>\r\n\r\n<p><strong>עד אמונים</strong> - ה<strong>עד</strong> אשר יתמיד לדבר <strong>אמונות</strong>, בודאי <strong>לא יכזב</strong> בדבר העדות; אבל הרגיל לדבר <strong>כזבים</strong>, ואם המה בדברים של מה בכך, מכל-מקום, הואיל ורגיל בכך <strong>יעיד</strong> גם-כן <strong>שקר</strong>, ויש לחקור הרבה בדבריו.</p>\r\n<h3><span class=\"mw-headline\" id=\".D7.9E.D7.A6.D7.95.D7.93.D7.AA_.D7.A6.D7.99.D7.95.D7.9F\">מצודת ציון</span></h3>\r\n<p><strong>ויפיח</strong> - ענין דבור.</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 14 AND `verse_number` = 5 AND 1 LIMIT 1;

