UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '<div style=\"background:#eef\">\r\n<p> -  - פסוק         <strong class=\"selflink\">ט</strong>                          </p>\r\n</div>\r\n\r\n<p><strong>יליץ אשם</strong> - ה<strong>אשם</strong> שמביא היא <strong>תליץ</strong> עליו להתכפר על מעלו; אבל <strong>בין ישרים</strong> יש ויש <strong>רצון</strong> ה\' ומבלי קרבן.</p>\r\n<h3><span class=\"mw-headline\" id=\".D7.9E.D7.A6.D7.95.D7.93.D7.AA_.D7.A6.D7.99.D7.95.D7.9F\">מצודת ציון</span></h3>\r\n<p><strong>יליץ</strong> - מלשון מליצה.</p>\r\n<p><strong>אשם</strong> - קרבן אשם.</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n', `mqorot` = '<br />', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 14 AND `verse_number` = 9 AND 1 LIMIT 1;

