UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '<div style=\"background:#eef\">\r\n<p> -  - פסוק             <strong class=\"selflink\">יג</strong>                      </p>\r\n</div>\r\n\r\n<p><strong>גם בשחוק</strong> - אפילו <strong>ב</strong>עבור רבוי ה<strong>שחוק</strong>, עם כי לא עבר בזה על מצות ה\', מכל-מקום <strong>יכאב לבו</strong> בעת יקבל הגמול על שפנה לבו לבטלה.</p>\r\n<p><strong>ואחריתה</strong> - ר\"ל: אם-כן סופה תהיה אשר ה<strong>שמחה</strong> תהפך ל<strong>תוגה</strong>.</p>\r\n<h3><span class=\"mw-headline\" id=\".D7.9E.D7.A6.D7.95.D7.93.D7.AA_.D7.A6.D7.99.D7.95.D7.9F\">מצודת ציון</span></h3>\r\n<p><strong>תוגה</strong> - מלשון יגון ועצבון.</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n', `mqorot` = '<br />', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 14 AND `verse_number` = 13 AND 1 LIMIT 1;

