UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '<div style=\"background:#eef\">\r\n<p> -  - פסוק                <strong class=\"selflink\">טז</strong>                   </p>\r\n</div>\r\n\r\n<p><strong>חכם ירא</strong> - ה<strong>חכם</strong> יפחד מדבר המפחיד ונשמר ממנו, ולזה הוא <strong>סר מן הרעה</strong> ולא תבוא עליו; אבל ה<strong>כסיל</strong> מתמלא <strong>עברה</strong> להתקצף על כל, <strong>ובוטח</strong> בעצמו שלא יאונה לו רעה, ולא כן הוא; וקיצר בדבר המובן מעצמו.</p>\r\n<h3><span class=\"mw-headline\" id=\".D7.9E.D7.A6.D7.95.D7.93.D7.AA_.D7.A6.D7.99.D7.95.D7.9F\">מצודת ציון</span></h3>\r\n<p><strong>מתעבר</strong> - מלשון עברה וכעס.</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n', `mqorot` = '<br />' WHERE `book` = 'משל' AND `chapter_number` = 14 AND `verse_number` = 16 AND 1 LIMIT 1;

