UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '<div style=\"background:#eef\">\r\n<p> -  - פסוק                 <strong class=\"selflink\">יז</strong>                  </p>\r\n</div>\r\n\r\n<p><strong>קצר אפים</strong> - מי שאינו מאריך אפו וממהר לכעוס, היא לו ל<strong>אולת</strong>.</p>\r\n<p><strong>ואיש מזמות</strong> - הנמשך אחר מחשבות לבו, לבל יפלס דבר הטובה היא אם רעה, יהיה <strong>שנוא</strong> לכל - לשמים ולבריות.</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n', `main_qod_subjects` = NULL, `mqorot` = '<br />', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 14 AND `verse_number` = 17 AND 1 LIMIT 1;

