UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '\r\n\r\n<p><strong>גם לרעהו</strong> (לעני כמותו) <strong>ישנא רש</strong>, עם כי הדרך להיות אוהבים בשווים בדבר מהדברים;&#160;&#160; <strong>ואוהבי עשיר רבים</strong> - בין עניים בין עשירים.</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n', `mqorot` = '<p>\r\nהמצב הכלכלי של האדם משפיע על יחסנו אליו -\r\nאנחנו מעדיפים להיות בחברתם של עשירים, שיכולים לעזור לנו, מאשר בחברתם של\r\nעניים, שרק מבקשים עזרה מאיתנו.</p>  \r\n<p>הפסוק שלנו מתאר את הנטייה הטבעית הזאת של האדם - \r\n<strong>לשנוא</strong>            את \r\n<strong>רעהו</strong>            \r\n<strong>העני</strong>, \r\n<strong>ולאהוב</strong>            את \r\n<strong>העשיר</strong>;&#160;</p> \r\n<p>הפסוק הבא ממשיך את הרעיון ומזהיר מפני נטיה זו (<a href=\"http://localhost/tnk1/ktuv/mj/14-21.html\">פירוט</a>).</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 14 AND `verse_number` = 20 AND 1 LIMIT 1;

