UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '<p><strong>בכל</strong> דבר <strong>עצבון</strong> ועמל יגיעת המלאכה <strong>יהיה</strong> לאדם <strong>יתרון</strong> וריוח מה;&#160;&#160; אבל <strong>דבר שפתים</strong>, העמל בו הוא <strong>אך למחסור</strong>, כי ברוב דברים לא יחדל פשע.</p>', `main_qod_subjects` = NULL WHERE `book` = 'משל' AND `chapter_number` = 14 AND `verse_number` = 23 AND 1 LIMIT 1;

