UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '<div style=\"background:#eef\">\r\n<p> -  - פסוק                          <strong class=\"selflink\">כו</strong>         </p>\r\n</div>\r\n\r\n<p><strong>ביראת ה\'</strong> - הליכת האדם <strong>ביראת ה\'</strong> היא לו <strong>מבטח עוז</strong>, וגם <strong>לבניו יהיה מחסה</strong> לחסות בצל זכותו.</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n', `main_qod_subjects` = NULL, `mqorot` = '<br />', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 14 AND `verse_number` = 26 AND 1 LIMIT 1;

