UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '<p><strong>צדקה תרומם גוי</strong> - אלו ישראל, כי המה <strong>יתרוממו</strong> בעבור ה<strong>צדקה</strong>;&#160;&#160; <strong>וחסד</strong> מעשה הצדקה של <strong>האומות</strong> יחשב עוד ל<strong>חטאת</strong>, כי גוזלים מזה ונותנים לזה.</p>' WHERE `book` = 'משל' AND `chapter_number` = 14 AND `verse_number` = 34 AND 1 LIMIT 1;

