UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '\r\n<p>להשכילו <strong>לנצור</strong> (לשמור) <strong>ארחות משפט</strong>, כי הוא <strong>ישמור דרך חסידיו</strong> לבל יטו מדרך האמיתי.\r\n\r\n\r\n\r\n</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 2 AND `verse_number` = 8 AND 1 LIMIT 1;

