UPDATE `sgulot` SET `mcudot` = '\r\n<p><strong>כ</strong>אשר <strong>תבא</strong> ה<strong>חכמה בלבך</strong>, <strong>והדעת</strong> תהיה <strong>נעימה</strong> (מתוקה וערבה)<strong> לנפשך</strong> להתאוות לה בחשק נפלא.</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 2 AND `verse_number` = 10 AND 1 LIMIT 1;

