UPDATE `sgulot` SET `prefix` = NULL, `tirgum` = '<p>אנשים <strong>פתיים</strong> מתפתים להאמין בהשקפת-עולם <strong>אוילית</strong> (שטחית), \"נתקעים\" בה כמו <strong>בנחלת</strong>-אבות, ואינם מסוגלים להחליף אותה;&#160;&#160; אולם אנשים <strong>ערומים</strong> (פיקחים) בוחנים כל&#160;<strong>מידע</strong> שהם מקבלים מכל הכיוונים כמו <strong>כתר</strong> המקיף את הראש; הם שולטים בעולם <strong>המידע</strong> כמו מלך שה<strong>כתר</strong> בראשו.</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 14 AND `verse_number` = 18 AND 1 LIMIT 1;

