UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '\r\n\r\n<p><strong>מרפא לשון</strong> - מי ש<strong>לשונו רפויה</strong> ולא ידבר קשות ורק ידבר רכות, היא לו ל<strong>עץ</strong> המגדל <strong>חיים</strong>. אבל המעקם בלשונו לדבר קשות, בא <strong>שבר</strong> ברצונו, כי לא יקובל דבריו ולא ימולא רצונו.</p>\r\n<h3><span class=\"mw-headline\" id=\".D7.9E.D7.A6.D7.95.D7.93.D7.AA_.D7.A6.D7.99.D7.95.D7.9F\">מצודת ציון</span></h3>\r\n<p><strong>מרפא</strong> - מלשון רפיון.</p>\r\n<p><strong>וסלף</strong> - ענין עיקום ועיוות.</p>\r\n<p><strong>ברוח</strong> - ענינו רצון, וכן ():</span> <strong>הנני נותן בו רוח</strong>.</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n' WHERE `book` = 'משל' AND `chapter_number` = 15 AND `verse_number` = 4 AND 1 LIMIT 1;

