UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '\r\n\r\n<p><strong>יזרו דעת</strong> - המה מפזרים את ה<strong>דעת</strong> ללמד לכל.</p>\r\n<p><strong>לא כן</strong> - ר\"ל <strong>לא כן</strong> יחפצו ללמוד כאשר החכמים חפצים ללמד.</p>\r\n<h3><span class=\"mw-headline\" id=\".D7.9E.D7.A6.D7.95.D7.93.D7.AA_.D7.A6.D7.99.D7.95.D7.9F\">מצודת ציון</span></h3>\r\n<p><strong>יזרו</strong> - ענין פיזור כמו ():</span> <strong>יזורה על נוהו גפרית</strong>.</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n', `mqorot` = '<br />', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 15 AND `verse_number` = 7 AND 1 LIMIT 1;

