UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '\r\n<p><strong>לב שמח</strong> - <strong>שמחת הלב</strong> <strong>תיטיב</strong> מאור <strong>הפנים</strong> וזהרו, <strong>וב</strong>עבור <strong>עצבת לב</strong> נשברה ה<strong>רוח</strong> וישפל בעיני עצמו.</p>\r\n<h3><span class=\"mw-headline\" id=\".D7.9E.D7.A6.D7.95.D7.93.D7.AA_.D7.A6.D7.99.D7.95.D7.9F\">מצודת ציון</span></h3>\r\n<p><strong>נכאה</strong> - נשברה, כמו (): <strong>ונכאה לבב</strong>.</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n', `main_qod_subjects` = NULL, `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 15 AND `verse_number` = 13 AND 1 LIMIT 1;

