UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '<p><strong>יסורים רעים</strong> מוכנים <strong>לעוזב אורח</strong> הנכון והראוי;&#160;&#160; <strong>ושונא תוכחת ימות</strong>.</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n', `mqorot` = '<p>פסוקים רבים בספר משלי מלמדים שיש \r\n<a href=\"http://localhost/tnk1/ktuv/mj/12-01.html\">לאהוב ביקורת</a>; אולם הפסוק שלנו קיצוני - \r\n<strong>שונא תוכחת ימות</strong>  - עד כדי כך?!</p><p>1. \"<q class=\"mfrj\">מי \r\n<strong>שעוזב דרך</strong>  ה\', צריך ליסרו \r\n<strong>במוסר רע</strong>  וקשה, כדי שיוסר; &#160; ומי \r\n<strong>ששונא תוכחת ימות</strong>  ברעתו, כי לא ישוב ממנה</q>\" <small>(<a class=\"external text\" href=\"http://he.wikisource.org/wiki/%D7%A8%D7%9C%D7%91%22%D7%92_%D7%A2%D7%9C_%D7%9E%D7%A9%D7%9C%D7%99_%D7%98%D7%95_%D7%99\">רלב\"ג</a>)</small>, כלומר - מי ששונא תוכחת, לא יזכה לחוות את חוויית השינוי והשיפור, אלא יישאר רע עד יום מותו;</p><p>2. \"<q class=\"mfrj\">ואשר לא יתעורר לקול המוכיחים - יוכפל עונו, כי הזהירוהו\r\n והקשה את לבו ולא נזהר... כי העובר עברה, תתקפהו התאוה\r\n והיצר השיאו, ויתכן כי נפשו מרה לו על אשר לא עצר כח מפני יצרו, ואולי \r\nיכסוף לתוכחת ויקוה למוסר. אבל <strong>שונא התוכחת</strong> כבר נואש מנפשו, ושנאת התוכחת \r\nתהיה לו לעדה כי הוא שונא דברי השם יתברך</q>\"\r\n<small class=\"small\"> (ר\' יונה גירונדי, שערי תשובה ב י-יא)</small>.</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 15 AND `verse_number` = 10 AND 1 LIMIT 1;

