UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '\r\n\r\n<p><strong>טוב מעט</strong> - יותר <strong>טוב מעט</strong> הון הבא <strong>ביראת ה\'</strong> וביושר,</p>\r\n<p><strong>מאוצר רב</strong> - <strong>ובו מהומת</strong> קול בני אדם צועקים על העושק אשר בו.</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n', `mqorot` = '<br />', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 15 AND `verse_number` = 16 AND 1 LIMIT 1;

