UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '<p><strong>כל ימי עני רעים</strong>, עם כי מצוי לו די מחסורו, כי יתקנא בעשירי העם ורעה עינו בשלהם;&#160;&#160; אבל מי שיש לו <strong>לב טוב</strong> לבל ירע עינו בזולתו, ישמח בחלקו, וימיו נדמים לו <strong>תמיד</strong> כימי <strong>משתה</strong> ושמחה.</p>\r\n', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 15 AND `verse_number` = 15 AND 1 LIMIT 1;

